import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})

export class AppComponent {
  title = 'todo';

  filter: 'all' | 'active' | 'done' = 'all';

  allItems = [
    { description: 'eat', done: true },
    { description: 'sleep', done: false },
    { description: 'play', done: false },
    { description: 'laugh', done: false },
  ];

  get items() {
    if (this.filter === 'all') {
      return this.allItems;
    }
    return this.allItems.filter(item => this.filter === 'done' ? item.done : !item.done);
  }

  addItem(description: string) {
    this.allItems.unshift({
      description,
      done: false
    })
  }

  remove(item) {
    this.allItems.splice(this.allItems.indexOf(item), 1);
  }  

}
<div class="container">
  <h1>My To Do List</h1>
  <label for="addItemInput">What will toy do today?</label>
  <input 
    id="addItemInput"
    class="lg-text-input"
    type="text"
    #newItem
    placeholder="Write your task"
    (keyup.enter) = 'addItem(newItem.value); newItem.value = ""'
  >  
  <button (click) = 'addItem(newItem.value); newItem.value = ""' class="btn-primary">Add</button>

  <h2>{{items.length}} <span *ngIf="items.length === 1; else elseBlock">item</span>
    <ng-template #elseBlock>items</ng-template></h2>

    <ul>
      <li *ngFor="let item of items">
        <app-item (remove)="remove(item)" [item]="item" *ngIf="item.done"></app-item>
      </li>
    </ul>    

</div>
